// -=-=-=-=-=-=-=-=-= FIXED HEADER AND SECTION NAVIGATION -=-=-=-=-=-=-=-=-=-=-

Tap.setupScrollHandler = function() {
  var $container = $( Tap.config.containerSelector ),
    containerBottom,
    containerHeight,
    containerTop,
    fixedClass = 'tap-control-fixed',
    lastSectionId = null,
    $nav = $container.find( '.tap-nav-container' ),
    navActiveClass = 'tap-nav-active',
    navHeight,
    navLeft,
    $search = $container.find( '.tap-search' ),
    searchHeight,
    searchLeft,
    searchWidth,
    sections = [
      {
        id: 'area-summary',
        nav: $nav.find( '.tap-nav-area-summary' ),
        section: $container.find( '.tap-content-section-area-summary' )
      },
      {
        id: 'dominant',
        nav: $nav.find( '.tap-nav-dominant' ),
        section: $container.find( '.tap-content-section-dominant' )
      },
      {
        id: 'density',
        nav: $nav.find( '.tap-nav-density' ),
        section: $container.find( '.tap-content-section-density' )
      },
      {
        id: 'download',
        nav: $nav.find( '.tap-nav-download' ),
        section: $container.find( '.tap-content-section-download' )
      },
      {
        id: 'about',
        nav: $nav.find( '.tap-nav-about' ),
        section: $container.find( '.tap-content-section-about' )
      }
    ],
    $window = $( window ),
    windowHalfHeight,
    windowHeight;

  // Ensure this is bound properly. This allows us to leave this detail out of
  // the overall init function.
  $window.unbind( 'resize.scrollHandler' );
  $window.bind( 'resize.scrollHandler', function( event ) {
    Tap.setupScrollHandler();
  });

  // Unbind the existing scroll handler, if any, and reset the search to its
  // non-fixed position so we can take the measurements we need.
  $window.unbind( 'scroll.scrollHandler' );
  $container.removeClass( fixedClass );
  $search.css({
    left: '',
    width: ''
  });
  $nav.css({
    left: ''
  });

  // Make all the measurements we're going to need in the scroll handler.
  containerHeight = $container.outerHeight();
  containerTop = $container.offset().top;
  containerBottom = containerTop + containerHeight;

  searchLeft = $search.offset().left;
  searchWidth = $search.width();
  searchHeight = $search.outerHeight();

  navLeft = $nav.offset().left;
  navHeight = $nav.outerHeight();

  windowHeight = $window.height();
  windowHalfHeight = windowHeight / 2;

  function measureSections() {
    var i,
      len,
      prevSectionDetails,
      section,
      sectionDetails;

    for ( i = 0, len = sections.length; i < len; i++ ) {
      sectionDetails = sections[ i ];
      section = sectionDetails.section;

      sectionDetails.top = section.offset().top - 10 - windowHalfHeight;
      sectionDetails.bottom = sectionDetails.top + section.height();

      if ( i === 0 ) {
        continue;
      }

      prevSectionDetails = sections[ i - 1 ];
      prevSectionDetails.bottom = sectionDetails.top;
    }
  }
  measureSections();

  // For some reason, this measurement can be inaccurate the first time. Make
  // the measurement one more time to be sure, but wait until you need it.
  $window.one( 'scroll.scrollHandler', measureSections );

  // Bind the scroll handler and run it once in order to ensure the elements
  // are positioned correctly.
  // Thanks: http://css-tricks.com/persistent-headers/
  function scrollHandler( event ) {
    var scrollTop = $window.scrollTop();

    if ( (scrollTop >= containerTop) &&
        (scrollTop < containerBottom - searchHeight) ) {
      // Keep everything fixed in its current position--we're somewhere
      // within the application container.
      $container.addClass( fixedClass );
      $search.css({
        left: searchLeft,
        width: searchWidth
      });
      $nav.css({
        left: navLeft
      });
      _.each( sections, function( sectionDetails ) {
        if ( (scrollTop >= sectionDetails.top) &&
            (scrollTop < sectionDetails.bottom) ) {
          sectionDetails.nav.addClass( navActiveClass );

          if ( lastSectionId !== sectionDetails.id ) {
            lastSectionId = sectionDetails.id;
            Tap.core.trigger( 'sectionChange', sectionDetails.id );
          }
        } else {
          sectionDetails.nav.removeClass( navActiveClass );
        }
      });
    } else if ( scrollTop >= containerBottom - searchHeight ) {
      // We're below the application container, so we'd normally keep things
      // at the bottom. Because the search sticks to the top of the window,
      // though, we're just going to clear all of the overrides, sending the
      // search right back to the top of the container.
      $container.removeClass( fixedClass );
      $search.css({
        left: '',
        width: ''
      });
      $nav.css({
        left: ''
      });
      _.each( sections, function( sectionDetails ) {
        sectionDetails.nav.removeClass( navActiveClass );
      });
      lastSectionId = null;
      Tap.core.trigger( 'sectionChange', null );
    } else {
      // Clear all of our style overrides--we're above the application
      // container.
      $container.removeClass( fixedClass );
      $search.css({
        left: '',
        width: ''
      });
      $nav.css({
        left: ''
      });
      _.each( sections, function( sectionDetails ) {
        sectionDetails.nav.removeClass( navActiveClass );
      });
      lastSectionId = null;
      Tap.core.trigger( 'sectionChange', null );
    }
  }
  $window.bind( 'scroll.scrollHandler', scrollHandler );
  scrollHandler();
};

