Tap.Area = Backbone.Model.extend({
  initialize: function() {
    var inSentenceForm;

    _.bindAll( this, 'compressOther', 'getType', 'select', 'topRaces', 'url' );

    inSentenceForm = (((this.id === '00' || this.id === '11') ? 'the ' : '') +
      this.get( 'shortName' ));
    this.set( 'inSentence', inSentenceForm );
  },
  url: function() {
    return Tap.config.dataRoot + '/areas/' + this.get( 'id' ) + '.json';
  },
  select: function() {
    Tap.core.trigger( 'selectArea', this );
    this.fetch({
      cache: true,
      dataType: 'jsonp',
      jsonp: false,
      jsonpCallback: 'area' + this.get( 'id' )
    });
  },
  topRaces: function() {
    var areaTotal,
      currentRaceId,
      currentRaceTotal,
      i,
      len,
      races,
      ranked,
      targetTotal,
      topRaceList,
      totals,
      totalSoFar;

    races = this.get( 'races' );
    if ( !races ) {
      return [];
    }

    totals = {};
    _.each( races, function( popByYear, raceId ) {
      totals[ raceId ] = _.reduce( popByYear, function( memo, population ) {
        return memo + population;
      }, 0 );
    });
    areaTotal = _.reduce( totals, function( memo, raceTotal ) {
      return memo + raceTotal;
    }, 0 );

    ranked = _.map( _.pairs( totals ).sort(function( a, b ) {
      // Sort in descending order by total.
      return b[ 1 ] - a[ 1 ];
    }), function( pair ) {
      // Just get the race IDs.
      return pair[ 0 ];
    });
    ranked = _.without( ranked, 'other' );

    topRaceList = [];
    totalSoFar = 0;
    targetTotal = Math.floor(
      areaTotal * Tap.config.chartOtherThreshold / 100 );
    for ( i = 0, len = ranked.length; i < len; i++ ) {
      currentRaceId = ranked[ i ];
      currentRaceTotal = totals[ currentRaceId ];

      totalSoFar += currentRaceTotal;
      topRaceList.push( currentRaceId );

      if ( totalSoFar >= targetTotal ) {
        break;
      }
    }

    return topRaceList;
  },
  compressOther: function( raceIdsToExclude ) {
    var compressed,
      populations;

    populations = this.get( 'races' );
    if ( !populations ) {
      return {};
    }

    compressed = {};
    _.each( populations, function( popByYear, raceId ) {
      if ( _.indexOf( raceIdsToExclude, raceId ) !== -1 ) {
        return;
      }

      _.each( popByYear, function( population, year ) {
        compressed[ year ] = (compressed[ year ] || 0) + population;
      });
    });

    return compressed;
  },
  getType: function() {
    if ( this.id === '00' ) {
      return 'nation';
    } else if ( this.id.length === 5 || this.id === '11' ) {
      return 'county';
    } else if ( this.id.length === 2 ) {
      return 'state';
    } else {
      return null;
    }
  }
});

