Tap.AreaResultView = Backbone.View.extend({
  tagName: 'li',
  className: 'tap-area-result',
  events: {
    'click': 'select',
    'mouseenter': 'highlightAndTrigger',
    'mouseleave': 'dehighlight'
  },
  initialize: function() {
    var areaName = this.model.get( 'name' ),
      areaNameCommaIndex,
      stateName;

    _.bindAll(
      this, 'dehighlight', 'hide', 'highlight', 'render', 'select', 'show',
      'template' );

    this.shown = false;

    areaNameCommaIndex = areaName.lastIndexOf( ', ' );
    if ( areaNameCommaIndex === -1 ) {
      stateName = areaName;
    } else {
      stateName = areaName.substring( areaNameCommaIndex + 2 );
    }
    this.stateName = stateName;

    function normalizeName( name ) {
      return name.toLowerCase().replace(
        /[^a-zA-Z ]/g, '' ).replace(
        /\bst\b/, 'saint' ).replace(
        /\bste\b/, 'sainte' );
    }

    this.normalizedNames = [ normalizeName( areaName ) ];
    _.each( this.model.get( 'aliases' ), function( name ) {
      this.normalizedNames.push( normalizeName( name ) );
      this.normalizedNames.push( normalizeName( name + ', ' + stateName ) );
    }, this );
    this.$aliases = {};

    this.model.on( 'change', this.render );
  },
  template: templates.JST[ 'AreaResultView.html' ],
  render: function() {
    this.$el.html( this.template({
      area: this.model,
      result: this
    }) );

    this.$( '.tap-area-result-alias' ).each( _.bind( function( i, aliasElem ) {
      var alias = this.normalizedNames[ 2 * i + 1 ],
        aliasWithState = this.normalizedNames[ 2 * i + 2 ];

      this.$aliases[ alias ] = $( aliasElem );
      this.$aliases[ aliasWithState ] = $( aliasElem );
    }, this ) );
    this.$aliasList = this.$( '.tap-area-result-aliases' );

    return this;
  },
  select: function() {
    this.model.select();
  },
  highlight: function() {
    this.$el.addClass( 'tap-area-result-highlighted' );
    this.highlighted = true;
  },
  highlightAndTrigger: function() {
    this.highlight();
    this.collectionView.highlightResult( this );
  },
  dehighlight: function() {
    this.$el.removeClass( 'tap-area-result-highlighted' );
    this.highlighted = false;
  },
  show: function( aliasToShow ) {
    var $aliasesToHide = $( '' ),
      $aliasToShow = this.$aliases[ aliasToShow ];

    _.each( this.$aliases, function( $alias, normalizedAlias ) {
      if ( !aliasToShow || normalizedAlias !== aliasToShow ) {
        $aliasesToHide = $aliasesToHide.add( $alias );
      }
    }, this );

    $aliasesToHide.removeClass( 'tap-area-result-alias-shown' );

    if ( $aliasToShow ) {
      $aliasToShow.addClass( 'tap-area-result-alias-shown' );
      this.$aliasList.addClass( 'tap-area-result-aliases-shown' );
    } else {
      this.$aliasList.removeClass( 'tap-area-result-aliases-shown' );
    }

    this.$el.addClass( 'tap-area-result-shown' );
    this.shown = true;
  },
  hide: function() {
    this.$el.removeClass( 'tap-area-result-shown' );
    this.shown = false;
  }
});

