Tap.AreaCollectionView = Backbone.View.extend({
  tagName: 'div',
  className: 'tap-area-collection',
  events: {
    'click': 'preventClose',
    'focus .tap-area-collection-search': 'clearSearch',
    'keyup .tap-area-collection-search': 'search'
  },
  initialize: function() {
    _.bindAll(
      this, 'clearSearch', 'closeSearch', 'filterResults', 'hideResults',
      'highlightResult', 'initResults', 'preventClose', 'render', 'scrollToResult',
      'search', 'showResults', 'template' );

    this.results = [];
    this.resultsShown = false;

    this.search = _.debounce( this.search, 100 );

    this.highlightedPrev = null;
    this.highlightedResult = null;
    this.highlightedNext = null;

    Tap.core.on( 'selectArea', this.closeSearch );
    $( 'html' ).on( 'click', this.closeSearch );
  },
  template: templates.JST[ 'AreaCollectionView.html' ],
  render: function() {
    this.$el.html( this.template({
      collection: this.collection
    }) );

    this.$results = this.$( '.tap-area-collection-results' );
    this.$noResults = this.$( '.tap-area-collection-noresults' );
    this.$search = this.$( '.tap-area-collection-search' );
    this.initResults();

    return this;
  },
  search: function( event ) {
    // When Escape is pressed, close the search and clear the field in case the
    // user wants to try again while the field still has focus.
    if ( event.keyCode === 27 ) {
      this.closeSearch();
      this.$search.val( '' );
      return;
    }

    // When Enter or Return is pressed, select the highlighted result and clean
    // up.
    if ( event.keyCode === 13 ) {
      if ( this.highlightedResult ) {
        this.highlightedResult.select();
      }
      return;
    }

    // When the up arrow is pressed, select the previous result (if any).
    if ( event.keyCode === 38 ) {  // Up
      if ( this.highlightedPrev == null ) {
        return;
      }
      this.scrollToResult( this.highlightedPrev );
      this.highlightResult( this.highlightedPrev );
      return;
    }

    // When the down arrow is pressed, select the next result (if any).
    if ( event.keyCode === 40 ) {  // Down
      if ( this.highlightedNext == null ) {
        return;
      }
      this.scrollToResult( this.highlightedNext );
      this.highlightResult( this.highlightedNext );
      return;
    }

    this.filterResults( this.$search.val() );
    if ( this.highlightedResult ) {
      this.scrollToResult( this.highlightedResult );
    }
  },
  closeSearch: function() {
    this.clearSearch();
    this.hideResults();
    this.$search.val( 'Search for county or state' );
  },
  clearSearch: function() {
    this.$search.val( '' );
    this.filterResults( '' );
  },
  filterResults: function( filter ) {
    var filterNormalized,
      firstShown,
      noResults = true;

    filterNormalized = filter.toLowerCase().replace(
      /[^a-zA-Z ]/g, '' ).replace(
      /\bst\b/, 'saint' ).replace(
      /\bste\b/, 'sainte' );

    _.each( this.results, function( result, index, results ) {
      var i,
        len,
        name,
        shouldHide;

      for ( i = 0, len = result.normalizedNames.length; i < len; i++ ) {
        name = result.normalizedNames[ i ];
        shouldHide = true;

        if ( name.indexOf( filterNormalized ) > -1 ) {
          noResults = false;
          result.show( name );
          shouldHide = false;

          if ( !firstShown ) {
            firstShown = result;
          }

          break;
        }
      }

      if ( shouldHide ) {
        result.hide();
      }
    });

    this.showResults( noResults );
    if ( filter === '' ) {
      this.highlightResult( null );
    } else {
      this.highlightResult( firstShown );
    }
  },
  initResults: function() {
    var area,
      i,
      len,
      result;

    if ( this.results.length ) {
      this.$results.empty();
      this.results.length = 0;
    }

    for ( i = 0, len = this.collection.models.length; i < len; i++ ) {
      area = this.collection.at( i );
      result = new Tap.AreaResultView({
        model: area
      });

      result.collectionView = this;
      result.render().hide();

      this.results.push( result );
      this.$results.append( result.$el );
    }
  },
  showResults: function( noResults ) {
    if ( noResults ) {
      this.hideResults();
      this.$noResults.addClass( 'tap-area-collection-noresults-shown' );
    } else {
      this.$results.addClass( 'tap-area-collection-results-shown' );
      this.$noResults.removeClass( 'tap-area-collection-noresults-shown' );
    }
    this.resultsShown = true;
    this.$el.addClass( 'tap-area-collection-menuactive' );
  },
  hideResults: function() {
    this.$results.removeClass( 'tap-area-collection-results-shown' );
    this.resultsShown = false;
    this.$el.removeClass( 'tap-area-collection-menuactive' );
  },
  highlightResult: function( highlightedResult ) {
    var highlightedResultId,
      i,
      len,
      shownResults,
      thisResult;

    // If there actually isn't any highlighted result (i.e., there aren't any
    // results at all), bail.
    if ( highlightedResult == null ) {
      this.highlightedResult = null;
      return;
    }

    // If the highlighted result isn't shown, tell it that it isn't as special
    // as it thinks it is, and get out of here.
    if ( !highlightedResult.shown ) {
      highlightedResult.dehighlight();
      this.highlightedResult = null;
      return;
    }

    // Dehighlight all results other than this one.
    _.each(
      _.without( this.results, highlightedResult ),
      function( result, index, results ) {
        result.dehighlight();
      });

    // Actually highlight the result.
    highlightedResult.highlight();

    // Keep track of the shown results before and after the highlighted result
    // in order to help the previous/next highlight methods.
    this.highlightedResult = highlightedResult;
    highlightedResultId = highlightedResult.model.get( 'id' );
    shownResults = _.filter( this.results, function( result ) {
      return result.shown;
    });
    for ( i = 0, len = shownResults.length; i < len; i++ ) {
      thisResult = shownResults[ i ];

      // Skip results until we get to the correct one.
      if ( thisResult.model.get( 'id' ) !== highlightedResultId ) {
        continue;
      }

      // Store the previous shown result for later use.
      if ( i > 0 ) {
        this.highlightedPrev = shownResults[ i - 1 ];
      } else {
        this.highlightedPrev = null;
      }

      // Store the next shown result for later use.
      if ( i < len - 1 ) {
        this.highlightedNext = shownResults[ i + 1 ];
      } else {
        this.highlightedNext = null;
      }
    }
  },
  preventClose: function( event ) {
    event.stopPropagation();
  },
  scrollToResult: function( result ) {
    var resultBottom,
      resultHeight,
      resultTop,
      resultsListBottom,
      resultsListHeight,
      resultsListTop;

    // Determine the currently visible area of the results list.
    resultsListTop = this.$results.scrollTop();
    resultsListHeight = this.$results.innerHeight();
    resultsListBottom = resultsListTop + resultsListHeight;

    // Scroll to the top of the list so we can tell how far down the result in
    // question is.
    this.$results.scrollTop( 0 );
    resultTop = result.$el.position().top;
    resultHeight = result.$el.outerHeight();
    resultBottom = resultTop + resultHeight;

    if ( resultBottom > resultsListBottom ) {
      // The result is below the currently visible area. Scroll it into view,
      // placing it at the bottom of the currently visible area.
      this.$results.scrollTop(
        resultTop - (resultsListHeight - resultHeight) );
    } else if ( resultTop < resultsListTop ) {
      // The result is above the currently visible area. Scroll it into view,
      // placing it at the top of the currently visible area.
      this.$results.scrollTop( resultTop );
    } else {
      // The result is within the currently visible area. Go back to where we
      // were.
      this.$results.scrollTop( resultsListTop );
    }
  }
});

