Tap.SplitMap = Backbone.View.extend({
  tagName: 'div',
  className: 'tap-map-wrap',
  initialize: function() {
    _.bindAll(
      this, 'render', 'setLocation', 'setOutline', 'setRaces', 'setSplit',
      'template', 'updateLayers', 'updateSplit' );

    this.topRace = 'white';
    this.bottomRace = 'black';
  },
  template: templates.JST[ 'SplitMap.html' ],
  render: function() {
    var baseLayer,
      baseLayerUrl,
      labelLayer,
      labelLayerUrl,
      $map,
      map,
      $split,
      $window = $( window );

    this.$el.html( this.template({}) );

    $map = this.$( '.tap-map' );
    $split = this.$( '.tap-map-split-control' );

    // Create an empty base layer to keep things from getting weird during data
    // layer changes.
    baseLayerUrl = (Tap.config.dataRoot +
      '/tiles-all-years/ages_PCT12H/5/4/13.png');
    baseLayer = new L.TileLayer( baseLayerUrl );

    // Create a layer of city labels.
    labelLayerUrl = Tap.config.dataRoot + '/tiles-cities/{z}/{x}/{y}.png';
    labelLayer = new L.TileLayer( labelLayerUrl );

    // Instantiate the Leaflet map.
    map = new L.Map( $map.get( 0 ), {
      attributionControl: false,
      center: new L.LatLng( 38.3, -96.5 ),
      fadeAnimation: false,
      layers: [ baseLayer, labelLayer ],
      maxZoom: 11,
      minZoom: 3,
      scrollWheelZoom: false,
      zoom: 4
    });

    // Add a layer control to the map.
    L.control.layers( null, {
      'City labels': labelLayer
    }, {
      collapsed: false,
      position: 'topleft'
    }).addTo( map );

    // Instantiate the slider, and bind it to this.updateSplit.
    $split.noUiSlider({
      behaviour: 'tap',
      handles: 1,
      orientation: 'vertical',
      range: [ 0, 100 ],
      set: this.updateSplit,
      slide: this.updateSplit,
      start: 50
    });

    this.$map = $map;
    this.$split = $split;
    this.labelLayer = labelLayer;
    this.map = map;

    this.updateLayers();

    $window.on( 'resize', _.bind(function() {
      map.invalidateSize({
        pan: false
      });
      this.updateSplit();
    }, this ) ).trigger( 'resize' );
    map.on( 'move', this.updateSplit );

    return this;
  },
  setLocation: function( area ) {
    var extents = area.get( 'extents' ),
      newBounds;

    newBounds = new L.LatLngBounds(
      new L.LatLng( extents[ 0 ][ 0 ], extents[ 0 ][ 1 ] ),
      new L.LatLng( extents[ 1 ][ 0 ], extents[ 1 ][ 1 ] ) );

    this.map.fitBounds( newBounds );
  },
  setOutline: function( area ) {
    var newLayer,
      outline = area.get( 'geojson' );

    if ( this.outlineLayer ) {
      this.map.removeLayer( this.outlineLayer );
    }

    newLayer = new L.GeoJSON( outline, {
      style: function( feature ) {
        return {
          clickable: false,
          color: '#fff',
          fill: false,
          opacity: 0.75,
          weight: 1
        };
      }
    });

    this.map.addLayer( newLayer );
    this.outlineLayer = newLayer;

    newLayer.bringToFront();
  },
  updateLayers: function() {
    var bottomLayer,
      bottomRaceTable,
      bottomTileUrl,
      dataRoot = Tap.config.dataRoot,
      errorTileUrl = dataRoot + '/tiles-all-years/ages_PCT12H/5/4/13.png',
      labelLayer = this.labelLayer,
      layerCreated = false,
      map = this.map,
      outlineLayer = this.outlineLayer,
      topLayer,
      topRaceTable,
      topTileUrl;

    topRaceTable = Tap.races.get( this.topRace ).get( 'tableId' );
    bottomRaceTable = Tap.races.get( this.bottomRace ).get( 'tableId' );

    topTileUrl = (dataRoot + '/tiles-all-years/ages_' + topRaceTable +
      '/{z}/{x}/{y}.png' );
    bottomTileUrl = ( dataRoot + '/tiles-all-years/ages_' + bottomRaceTable +
      '/{z}/{x}/{y}.png' );

    if ( this.bottomLayer ) {
      this.bottomLayer.setUrl( bottomTileUrl );
    } else {
      bottomLayer = new L.TileLayer( bottomTileUrl, {
        errorTileUrl: errorTileUrl
      });
      map.addLayer( bottomLayer );
      this.bottomLayer = bottomLayer;
      this.bottomContainer = bottomLayer.getContainer();
      layerCreated = true;
    }

    if ( this.topLayer ) {
      this.topLayer.setUrl( topTileUrl );
    } else {
      topLayer = new L.TileLayer( topTileUrl, {
        errorTileUrl: errorTileUrl
      });
      map.addLayer( topLayer );
      this.topLayer = topLayer;
      this.topContainer = topLayer.getContainer();
      layerCreated = true;
    }

    // Move the city labels and area outline to the top if needed.
    if ( layerCreated ) {
      if ( map.hasLayer( labelLayer ) ) {
        labelLayer.bringToFront();
      }
      if ( outlineLayer && map.hasLayer( outlineLayer ) ) {
        outlineLayer.bringToFront();
      }
    }
  },
  setRaces: function( topRace, bottomRace ) {
    this.topRace = topRace;
    this.bottomRace = bottomRace;
    this.updateLayers();
  },
  setSplit: function( newSplit ) {
    // Thanks: https://www.mapbox.com/mapbox.js/example/v1.0.0/swipe-layers/

    var clipBottom,
      clipLeft,
      clipRight,
      clipTop,
      map = this.map,
      mapNW = map.containerPointToLayerPoint([ 0, 0 ]),
      mapSE = map.containerPointToLayerPoint( map.getSize() );

    clipLeft = mapNW.x;
    clipRight = mapSE.x;
    clipTop = mapNW.y;

    clipBottom = clipTop + (mapSE.y - clipTop) * newSplit / 100;

    this.topContainer.style.clip = ('rect(' +
      clipTop + 'px,' +
      clipRight + 'px,' +
      clipBottom + 'px,' +
      clipLeft + 'px)');
  },
  updateSplit: function() {
    this.setSplit( this.$split.val() );
  }
});

