  tour = new Tap.Tour({
    collection: tourSteps
  });

  // Check for the cookie and start the tour if it isn't found.
  cookiePresent = document.cookie.replace(
    new RegExp( '(?:(?:^|.*;\\s*)' + cookieName + '\\s*\\=\\s*([^;]*).*$)|^.*$' ),
    '$1' ) === cookieValue;
  if ( !cookiePresent ) {
    tour.start();
  } else {
    // TODO: Remove this before release.
    tour.start();
  }

  // Set the cookie.
  document.cookie = (cookieName + '=' + cookieValue +
    ';max-age=' + cookieAge.toString( 10 ));

  // Allow the user to start the tour manually.
  $startElem.on( 'click', function( event ) {
    event.preventDefault();
    tour.start();
  });
