// -=-=-=-=-=-=-=-=-=-=-=-=-=-=-= URL ROUTING -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-

Tap.Router = Backbone.Router.extend({
  initialize: function( options ) {
    var optionDelimiter = '-',
      routeDelimiter = '/',
      routeRegExp,
      routeRegExpString = '';

    _.bindAll( this, 'createRoute', 'updateViews' );

    this.density = options.density;
    this.currentState = {};

    // Current area FIPS
    // NOTE: The delimiter should be optional here. If it's a slash, Backbone
    // will strip it out when the FIPS is empty (i.e., the finished route
    // starts with a slash). If that's the case, the resultant route won't
    // match this regex and we'll end up missing out on the user's desired
    // destination.
    routeRegExpString += '([0-9]{2}|[0-9]{5})';  // State or county FIPS code
    routeRegExpString += routeDelimiter + '?';

    // Map races
    routeRegExpString += '([0-9]+)-([0-9]+)';  // Pair of race indices

    routeRegExp = new RegExp( routeRegExpString );
    this.route( routeRegExp, 'updateViews' );

    this.optionDelimiter = optionDelimiter;
    this.routeDelimiter = routeDelimiter;
  },
  updateViews: function() {
    // Doing this through arguments array to avoid cluttering up the function
    // declaration.
    var areaId = arguments[ 0 ],
      bottomRaceIndex = parseInt( arguments[ 2 ], 10 ),
      density = this.density,
      maps = density.maps,
      prevState = this.currentState,
      topRaceIndex = parseInt( arguments[ 1 ], 10 ),
      updateAllExceptArea;

    // Compare the old state to the new state to figure out what's changed, and
    // update everything to match.
    updateAllExceptArea = _.bind(function updateAllExceptArea() {
      if ( prevState.topRaceIndex !== topRaceIndex ) {
        density.topRace.options[ topRaceIndex ].highlight();
      }
      if ( prevState.bottomRaceIndex !== bottomRaceIndex ) {
        density.bottomRace.options[ bottomRaceIndex ].highlight();
      }

      // Update our saved copy of the application state.
      this.currentState = {
        areaId: areaId,
        topRaceIndex: topRaceIndex,
        bottomRaceIndex: bottomRaceIndex
      };
    }, this );

    // Kick it off by looking up the area if we need one (i.e., there isn't a
    // current area yet to fall back on) and waiting for more detailed data if
    // necessary to proceed.
    if ( prevState.areaId !== areaId ) {
      Tap.core.on( 'selectArea', function( area ) {
        if ( area.get( 'races' ) ) {
          updateAllExceptArea();
        } else {
          area.once( 'change:races', updateAllExceptArea, this );
        }
      }, this );

      Tap.areas.get( areaId ).select();
    } else {
      updateAllExceptArea();
    }
  },
  createRoute: function() {
    var areaId,
      bottomRaceIndex,
      density = this.density,
      optionDelimiter = this.optionDelimiter,
      route = '',
      routeDelimiter = this.routeDelimiter,
      topRaceIndex;

    // Current area FIPS
    areaId = Tap.core.area.id;

    route += areaId;
    route += routeDelimiter;

    // Map races
    function getHighlightedRaceIndex( raceSelectorView ) {
      var i,
        len,
        option,
        options = raceSelectorView.options;

      for ( i = 0, len = options.length; i < len; i++ ) {
        option = options[ i ];

        if ( option.highlighted ) {
          return i;
        }
      }
    }

    topRaceIndex = getHighlightedRaceIndex( density.topRace );
    bottomRaceIndex = getHighlightedRaceIndex( density.bottomRace );

    route += topRaceIndex.toString( 10 );
    route += optionDelimiter;
    route += bottomRaceIndex.toString( 10 );

    // Save the state of each route component so we can determine later what
    // changed.
    this.currentState = {
      areaId: areaId,
      topRaceIndex: topRaceIndex,
      bottomRaceIndex: bottomRaceIndex
    };

    return route;
  }
});

