Tap.init = function init() {
	console.log("tap init");
  var areaDetail,
    areaSummary,
    autocomplete,
    $container = $( Tap.config.containerSelector ),
    density,
    densitySummary,
    dominantSummary,
    download,
    interesting,
    $pageContents = $( 'html, body' ),
    popChart,
    search,
    $searchHeader = $( '.tap-search' ),
    $window = $( window );

  // Add Leaflet conditional comment.
  // TODO: See whether this works.
  $( 'head' ).append( '<!--[if lte IE 8]><link rel="stylesheet" ' +
    'href="http://cdn.leafletjs.com/leaflet-0.6.4/leaflet.ie.css" />' +
    '<![endif]-->' );

  // Initialize the individual components.
  autocomplete = new Tap.AreaCollectionView({
    collection: Tap.areas,
    el: $( '#tap-search-autocomplete' )
  });
  autocomplete.render();

  areaSummary = new Tap.AreaSummaryView({
    el: $( '#tap-area-summary' )
  });
  areaSummary.$el.html( '' );

  areaDetail = new Tap.AreaDetailView({
    el: $( '#tap-search-showing' )
  });
  areaDetail.$el.html( '&nbsp;' );

  density = new Tap.DensityView({
    el: $( '#density' ),
    $bottomRaceEl: $( '#tap-density-bottom-races' ),
    $mapsEl: $( '#tap-density-maps' ),
    $topRaceEl: $( '#tap-density-top-races' )
  });

  densitySummary = new Tap.DensitySummary({
    el: $( '#tap-density-summary' )
  });

  popChart = new Tap.PopChart({
    el: $( '#dominant' ),
    $chartEl: $( '#tap-dominant-chart' ),
    $racesEl: $( '#tap-dominant-races' )
  });

  dominantSummary = new Tap.DominantSummary({
    el: $( '#tap-dominant-summary' )
  });

  download = new Tap.Download({
    el: $( '#tap-download' )
  });
  download.render();

  search = new Tap.SearchView({
    collection: Tap.search,
    el: $( '#tap-advanced-search' ),
    $modalTrigger: autocomplete.$( '.tap-area-collection-advanced' )
  });
  search.render();

  interesting = new Tap.Interesting({
    collection: Tap.interesting,
    el: $( '#tap-interesting' )
  });
  interesting.render();

  // Connect components as needed.

  // Render the area header and summary.
  Tap.core.on( 'selectArea', function( area ) {
    areaDetail.model = area;
    areaDetail.render();
    areaSummary.model = area;
    areaSummary.render();
  });

  // Update the map summary.
  function updateSummary() {
    densitySummary.render(
      Tap.core.area,
      density.topRace.highlightedOption.model,
      density.bottomRace.highlightedOption.model );
  }

  Tap.core.on( 'selectArea', function( area ) {
    if ( area.get( 'densities' ) ) {
      updateSummary();
    } else {
      area.once( 'change:densities', updateSummary, area );
    }
  });

  density.topRace.on( 'highlightOption', updateSummary );
  density.bottomRace.on( 'highlightOption', updateSummary );

  // Update the population summary.

  function updateDominantSummary() {
    dominantSummary.render(
      Tap.core.area,
      popChart.raceSelector.collection.get(
        popChart.raceSelector.maxRaceId ) );
  }

  Tap.core.on( 'selectArea', function( area ) {
    if ( area.get( 'races' ) ) {
      updateDominantSummary();
    } else {
      area.once( 'change:races', updateDominantSummary, area );
    }
  });

  $window.resize( _.throttle( popChart.chart.render, 100 ) );

  // Initialize scroll handler.
  function attemptScrollHandlerSetup() {
    try {
      Tap.setupScrollHandler();
    } catch ( e ) {
      window.setTimeout( attemptScrollHandlerSetup, 100 );
    }
  }
  attemptScrollHandlerSetup();

  // There were some odd conflicts with map initialization when I set the
  // random starting location immediately at the end of init, so I'm waiting
  // half a second first.
  window.setTimeout(function() {
    var router = new Tap.Router({ density: density });

    // Hook up the various application components to the router.
    function updateRoute() {
      router.navigate( router.createRoute() );
    }
    Tap.core.on( 'selectArea', updateRoute );
    density.on( 'updateRaces', updateRoute );

    if ( !Backbone.history.start() ) {
      Tap.selectInitialLocation();
    }
  }, 500 );

  // Set up the page navigation.
  $( '.tap-nav a[href!="#"]' ).on( 'click', function( event ) {
    var nextSelector;

    event.preventDefault();

    nextSelector = event.currentTarget.getAttribute( 'href' );
    if ( nextSelector ) {
      // Thanks:
      // http://www.paulund.co.uk/smooth-scroll-to-internal-links-with-jquery
      $pageContents.stop().animate({
        'scrollTop': $( nextSelector ).offset().top
      }, 1000 );
    }
  });

  // Close the autocomplete results when the advanced search button is clicked.
  search.$modalTrigger.on( 'click', function( event ) {
    event.preventDefault();
    autocomplete.closeSearch();
  });

  // Initialize the tour.
  Tap.initTour(
    [
      {
        $focus: autocomplete.$el,
        toSide: 'bottom',
        edgeToAlign: 'right',
        title: 'Search',
        text: ('Start by searching for an area. You can search for a state, ' +
          'a county, or a city. You also can do an advanced search to find ' +
          'an area based on demographic characteristics.')
      },
      {
        $focus: areaSummary.$el,
        toSide: 'left',
        edgeToAlign: 'top',
        title: 'Key characteristics',
        text: ('See the key demographic characteristics about each area and ' +
          'what makes that area unique.')
      },
      {
        $focus: popChart.chart.$el,
        toSide: 'left',
        edgeToAlign: 'top',
        title: 'Demographic chart',
        text: ('Here you can see the changing demographics of future ' +
          'college students, from those who are near college age on the ' +
          'left, to those who are still 14 years away on the right.')
      },
      {
        $focus: density.maps.$el,
        toSide: 'left',
        edgeToAlign: 'top',
        title: 'Map',
        text: ('The map makes it easy to compare the population densities ' +
          'of different ethnicities. Use the tabs to switch groups, or drag ' +
          'the line to see the split between them.')
      },
      {
        $focus: download.$el,
        toSide: 'left',
        edgeToAlign: 'top',
        title: 'Download data',
        text: ('Want to play with the data on your own? Just download the ' +
          'data here.')
      },
      {
        $focus: $( '.tap-content-section-about p' ).first(),
        toSide: 'left',
        edgeToAlign: 'top',
        title: 'About this project',
        text: 'This is where we explain our methodology and our sourcing.'
      }
    ],
    'tourProspects20130103',
    $( '.tap-nav-tour a' )
  );

  // Mark that initialization has happened.
  Tap.config.initOccurred = true;
};
/*from init.js*/
function init() {
  Tap = window.Tap;

  if ( Tap && Tap.config ) {
    if ( !Tap.config.initOccurred && !Tap.config.avoidInit ) {
      $( window.document ).ready(function() {
        if ( $( '.tap-advanced-search' ).length !== 0 ) {
          Tap.init();
        }
      });
    }

    return;
  }

  window.setTimeout( init, 100 );
}
init();
